<template>
  <a-dropdown
    v-model:open="dropdownMenuDisplayHidden"
    :trigger="['click']"
    :destroyPopupOnHide="true"
    placement="bottom"
    arrow
    class="w-[275px] dropDownList pl-[20px]"
  >
    <div class="ant-dropdown-link flex items-center" @click.prevent>
      <span
        class="min-w-[60px] max-w-[223px] line-clamp-1 leading-[100%] text-[14px] text-[#606266]"
        >{{ currentProjectName }}</span
      >
      <Icon
        icon="ep:arrow-up-bold"
        :rotate="2"
        class="ml-[10px] text-[#818285]"
      />
    </div>
    <template #overlay>
      <a-menu class="w-[275px]">
        <a-input
          v-model:value="projectSearchvalue"
          placeholder="搜索"
          class="h-[36px]"
        >
          <template #prefix>
            <Icon
              class="mr-[5px] text-[#cec6d6]"
              icon="simple-line-icons:magnifier"
            />
          </template>
        </a-input>
        <div
          class="max-h-[500px] overflow-x-hidden overflow-y-auto mt-[10px] cursor-pointer scrollable-container"
        >
          <div
            v-for="item in dataOngoingProject"
            :key="item.id"
            class="flex items-center h-[60px] text-[#606266] hover:bg-[#ecf5ff] hover:text-[#66b1ff] relative"
            @click="
              () => {
                router.push(`/projectManagement/project/${item.id}`);
                dropdownMenuDisplayHidden = false;
              }
            "
          >
            <a-image
              class="object-cover rounded-[50%]"
              style="height: 32px"
              :preview="false"
              :width="32"
              :height="32"
              :src="`http://api.cc0820.top:7001${item.cover}`"
              fallback="data:image/png;base64,"
              v-if="item.cover.includes('/public/uploads/')"
            />
            <a-image
              class="object-cover rounded-[50%]"
              style="height: 32px"
              :preview="false"
              :width="32"
              :height="32"
              :src="item.cover"
              fallback="data:image/png;base64,"
              v-else-if="item.cover.includes('https://')"
            />
            <a-image
              class="object-cover rounded-[50%]"
              style="height: 32px"
              :preview="false"
              :width="32"
              :height="32"
              src="data:image/png;base64,"
              fallback="data:image/png;base64,"
              v-else
            />
            <div class="w-[140px] leading-[20px] pl-[10px] line-clamp-1">
              {{ item.name }}
            </div>
            <Icon
              v-if="currentlyDisplayedItemsID === item.id"
              icon="icon-park-solid:correct"
              class="absolute top-[50%] right-[10px] translate-x-[-50%] translate-y-[-50%]"
            />
          </div>
        </div>
      </a-menu>
    </template>
  </a-dropdown>
</template>

<script lang="ts" setup>
import { ongoingProject } from "@/service/index"; // 导入服务模块
import { onBeforeRouteUpdate } from "vue-router";

onBeforeRouteUpdate((to, from, next) => {
  refresh();
  next();
});

// 获取路由实例
const router = useRouter();

// 路由解析
const route = useRoute();

// 项目搜索
const projectSearchvalue = ref<string>("");
const currentProjectName = ref<string>(""); //  当前项目名称
const currentlyDisplayedItemsID = ref<number>(); // 当前显示的项目
const dropdownMenuDisplayHidden = ref<boolean>(false); // 下拉菜单显示隐藏

// 请求 获取进行中的项目
const { data: dataOngoingProject, refresh } = useRequest(
  () =>
    ongoingProject({
      is_recycle: 0,
      is_archived: 0,
    }),
  {
    onSuccess: () => {
      dataOngoingProject.value?.find((item) => {
        if (item.id === Number(route.params.id)) {
          currentProjectName.value = item.name;
          currentlyDisplayedItemsID.value = item.id;
        }
      });
    },
  },
);
</script>

<style lang="stylus" scoped></style>
